import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Spinner } from 'react-bootstrap';

const Wrapped = styled.div`
    position: relative;
    overflow: hidden;
    width: 100%;
    height: 100%;
    background-image: url('https://picsum.photos/1000/1000');
    filter: blur(10px);
    transition: opacity 1s ease-in-out;
    opacity: ${({ loaded }) => (loaded ? 0 : 1)};
    &:before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: url('https://picsum.photos/1000/1000');
        opacity: 0.5;
        filter: blur(50px);
    }
    &:after {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: url('https://picsum.photos/1000/1000');
        opacity: 0.5;
        filter: blur(100px);
    }
`;

const GaussianBlurWrapper = () => {
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setLoaded(true);
        }, 5000);
        return () => clearTimeout(timer);
    }, []);

    return (
        <Wrapped loaded={loaded}>
            {!loaded && (
                <div
                    style={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                    }}
                >
                    <Spinner animation="border" variant="light" />
                </div>
            )}
        </Wrapped>
    );
};

export default GaussianBlurWrapper;
