import React,{ useState } from "react";
import "./App.css";

function DropdownList({ numDropdowns }) {
  const dropdowns = [...Array(numDropdowns)].map((_, i) => (
    <select key={i} className="center-dropdown">
      <option value="" disabled>
        None
      </option>
      {[...Array(9)].map((_, j) => (
        <option key={j} value={j + 1}>
          {j + 1}
        </option>
      ))}
    </select>
  ));

  return <div className="dropdown-list">{dropdowns}</div>;
}

function App() {
  const [selectedValue, setSelectedValue] = useState("");

  const handleChange = (event) => {
    setSelectedValue(event.target.value);
  };

  const handleNext = (event) => {
    if (selectedValue === "") {
      event.preventDefault();
    } else {
      console.log("Selected value:", selectedValue);
    }
  };

  return (
    <div className="center-container">
      <>
      <label htmlFor="select-subjects">Select Subjects:</label>

        <h1 className="center">Select Number of Subjects</h1>
        <select
          value={selectedValue}
          onChange={handleChange}
          className="center-dropdown"
        >
          <option value="" disabled>
            None
          </option>
          {[...Array(9)].map((_, i) => (
            <option key={i} value={i + 1}>
              {i + 1}
            </option>
          ))}
        </select>
        <button className="next-button" onClick={handleNext}>
          Next
        </button>
      </>
      <>
        <label htmlFor="select-subjects">Select Subjects:</label>
        {selectedValue !== "" && (
          <DropdownList numDropdowns={parseInt(selectedValue)} />
        )}
      </>
    </div>
  );
}

export default App;
