import React,{ useState } from "react";
import "./App.css";

function DropdownList({ numDropdowns }) {
  const dropdowns = [...Array(numDropdowns)].map((_, i) => (
    <select key={i} className="center-dropdown">
      <option value="" disabled>
        None
      </option>
      {[...Array(9)].map((_, j) => (
        <option key={j} value={j + 1}>
          {j + 1}
        </option>
      ))}
    </select>
  ));

  return <div className="dropdown-list">{dropdowns}</div>;
}

function App() {
  const [selectedValue, setSelectedValue] = useState("");
  const [nextClicked, setNextClicked] = useState(false);

  const handleChange = (event) => {
    setSelectedValue(event.target.value);
  };

  const handleNext1 = (event) => {
    if (selectedValue === "") {
      event.preventDefault();
    } else {
      console.log("Selected value:", selectedValue);
      setNextClicked(true);
    }
  };

  const handleBack = () => {
    setNextClicked(false);
  };

  return (
    <div className="center-container">
      {!nextClicked && (
        <>
          <h1 className="center">Select Number of Subjects</h1>
          <select
            value={selectedValue}
            onChange={handleChange}
            className="center-dropdown"
          >
            <option value="" disabled>
              None
            </option>
            {[...Array(9)].map((_, i) => (
              <option key={i} value={i + 1}>
                {i + 1}
              </option>
            ))}
          </select>
          <button className="next-button" onClick={handleNext1}>
            Next
          </button>
        </>
      )}
      {nextClicked && (
        <>
          <div>
      <label htmlFor="num-dropdowns">Select Number of Dropdowns:</label>
      <select id="num-dropdowns" value={numDropdowns} onChange={handleNumDropdownsChange}>
        {[...Array(9)].map((_, i) => (
          <option key={i} value={i + 1}>
            {i + 1}
          </option>
        ))}
      </select>
      <DropdownList numDropdowns={numDropdowns} />
    </div>
          <button className="next-button-right" onClick={handleNext1}>
            Next
          </button>
          <button className="back-button-left" onClick={handleBack}>
            Back
          </button>
        </>
      )}
    </div>
  );
}

export default App;
